import { fetchProducts } from '../helpers/fetchData';
import {useSelector} from 'react-redux'

const Pagination = () => {


    const total = useSelector((state) => state.total)
    const perPage = 9
    const numOfPages = Math.ceil(total / perPage)
    const pages = []
    for (let i = 1; i <= numOfPages; i++){
        pages.push(i)
    }

    const handlePageChange = (number) => {
        console.log(number)
    }

    const renderPageNumbers = () => {
        pages.map((number) => {
            `<span><a className="page-link" href='#" onClick=({handlePageChange(${number}}) ${number}</a></span>`
        })

    }


    return (
        <div className='page-numbers-row'>
            {renderPageNumbers()}
        </div>
    )

}

export default Pagination;
